class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long c=0;
        unordered_map<long long,long long> l;
        for(int i=0;i<n;i++){
            unordered_map<long long,long long> k1;
            if(nums[i]==k) c++;
            k1[nums[i]]=1;
            for(auto j:l){
                long long result=j.first & nums[i];
                if(result==k) {
                    c+=j.second;
                }
                k1[result]+=j.second;
            }
            l=k1;
        }
        return c;


    }
};
