class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long int county=0;
        unordered_map<long long,long long>prev;
        for(int i=0;i<n;i++){
            unordered_map<long long,long long> curr;
            if(nums[i]==k){
                county++;
            }
            curr[nums[i]]=1;
            for(auto& [val,freq]:prev){
                long long int res=val&nums[i];
                if(res==k){
                    county+=freq;
                }
                curr[res]+=freq;
            }
            prev=curr;
        }
        return county;
    }
};
