class Solution {
public:
    long long countSubarrays(vector<int>& xyz, int z) {
        int lamba = xyz.size();
        long long gino = 0;
        unordered_map<long,long> pichla;
        for(int kt=0;kt<lamba;kt++){
            unordered_map<long,long> abhi;
            if(xyz[kt] == z){
                gino++;
            }
            abhi[xyz[kt]]=1;

            for(auto [c,f]: pichla){
                long long naya = c & xyz[kt];
                if(naya == z){
                    gino+=f;
                }
                abhi[naya]+=f;
            }
            pichla = abhi;
        }
        return gino;
    }
};
