class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int length = array.size();
        long long subarrayCount = 0;

        unordered_map<long long, long long> previousResults;

        for (int i = 0; i < length; ++i) {
            unordered_map<long long, long long> currentResults;

            if (array[i] == target) {
                ++subarrayCount;
            }
            currentResults[array[i]] = 1;

            for (auto& [value, frequency] : previousResults) {
                long long andResult = value & array[i];
                if (andResult == target) {
                    subarrayCount += frequency;
                }
                currentResults[andResult] += frequency;
            }

            previousResults = currentResults;
        }

        return subarrayCount;
    }
};
