class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int,long long>p;
        //long long res=0;
        long long cnt=0;

        for(int i=0;i<nums.size();i++)
        {
            unordered_map<int,long long>q;
            if(nums[i]==k)
            {
                cnt++;
            }
            //cout<<cnt<<endl;

            q[nums[i]]=1;

            for(auto it=p.begin();it!=p.end();++it)
            {
                long long res=it->first & nums[i];
                if(res==k)
                    cnt+=it->second;

                q[res]+=it->second;
                //cout<<q[res]<<endl;
            }

            p=q;
        }
        return cnt;
    }
};
