typedef long long ll;

class Solution {
public:
    ll countSubarrays(std::vector<int>& nums, int aim) {
        int m = nums.size();
        ll res = 0;
        std::unordered_map<ll, ll> prevres;

        for (int index = 0; index < m; ++index) {
            std::unordered_map<ll, ll> curr_res; 
            if (nums[index] == aim) {
                ++res;
            }
            curr_res[nums[index]] = 1;
            for (auto& [value, f] : prevres) {
                ll it = value & nums[index];
                if (it == aim) {
                    res += f;
                }
                curr_res[it] += f;
            }

            prevres = curr_res;
        }
        return res;
    }
};
