class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int size = array.size();
        long long total = 0;
        unordered_map<long long, long long> prevR;
        for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> curR; 
            if (array[i] == target) {
                ++total;
            }
            curR[array[i]] = 1;
            for (auto& [value, frequency] : prevR) {
                long long nAR = value & array[i];
                if (nAR == target) {
                    total += frequency;
                }
                curR[nAR] += frequency;
            }

            prevR = curR;
        }
        return total;
    }
};
