class Solution {
public:
    long long countSubarrays(vector<int>& arr, int t) {
        int size = arr.size();
        long long total = 0;
        unordered_map<long long, long long> p;
        for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> c; 
            if (arr[i] == t) {
                total++;
            }
            c[arr[i]] = 1;
            for (auto& [v, f] : p) {
                long long n = v & arr[i];
                if (n == t) {
                    total += f;
                }
                c[n] += f;
            }

            p = c;
        }
        return total;

    }
};
