class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long res = 0;
        unordered_map<long long, long long> prev;

        for(int i = 0;i < n;i++) {
            unordered_map<long long, long long> curr; 
            if (nums[i] == k) {
                res++;
            }
            curr[nums[i]] = 1;
            for (auto it : prev) {
                long long tmp = it.first & nums[i];
                if (tmp == k) {
                    res += it.second;
                }
                curr[tmp] =curr[tmp] + it.second;
            }

            prev = curr;
        }
        return res;

    }
};
