class Solution {
public:
    #define ll long long
    long long countSubarrays(vector<int>& nums, int k) {
        ll n = nums.size();
        ll cnt = 0;
        map<ll,ll>prev;
        for(int i=0;i<n;i++){
            map<ll,ll>curr;
            if(nums[i] == k) cnt++;
            curr[nums[i]]++;
            for(auto it:prev){
                ll val = (it.first & nums[i]);
                if(val == k) cnt += it.second;
                curr[val] += it.second;
            }
            prev = curr;
        }
        return cnt;
    }
};
