class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        long long count = 0;

        unordered_map<long long, long long> prevSub;

        for (int i = 0; i < size; i++) {
            unordered_map<long long, long long> currSub; 

            if (nums[i] == k) {
                ++count;
            }
            currSub[nums[i]] = 1;

            for (auto& [val, freq] : prevSub) {
                long long andRes = val & nums[i];
                if (andRes == k) {
                    count += freq;
                }
                currSub[andRes] += freq;
            }

            prevSub = currSub;
        }

        return count;
    }
};
