class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int sz = nums.size();
        long long tot = 0;
        unordered_map<long long, long long> prev;

        for (int idx = 0; idx < sz; ++idx) {
            unordered_map<long long, long long> curr;

            if (nums[idx] == k) {
                ++tot;
            }

            curr[nums[idx]] = 1;

            for (auto& [value, frequency] : prev) {
                long long newAndResult = value & nums[idx];

                if (newAndResult == k) {
                    tot += frequency;
                }

                curr[newAndResult] += frequency;
            }

            prev = curr;
        }

        return tot;
    }
};
