class Solution {
public:
    long long countSubarrays(vector<int>& ar, int t) {
     int size = ar.size();
     long long a = 0;
     unordered_map<long long, long long> mp;
     for (int it = 0; it < size; ++it) {
         unordered_map<long long, long long> mpp; 
         if (ar[it] == t) {
             ++a;
         }
         mpp[ar[it]] = 1;
         for (auto& [value, frequency] : mp) {
             long long nAr = value & ar[it];
             if (nAr == t) {
                 a += frequency;
             }
             mpp[nAr] += frequency;
         }

         mp = mpp;
     }
     return a;

    }
};
