class Solution {
public: 
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long int ans = 0;
       map<long long int,long long int>m; 
        for (auto x:nums)
        {
            map<long long int, long long int>mpp; 
            if (x==k)ans++;
            mpp[x]=1;
             for(auto it:m)
            {
                long long int res =it.first& x;
                if (res==k)
                {
                    ans+=it.second;
                }
                mpp[res]+=it.second;
            }

            m=mpp;
        }

        return ans;
    }
};
