class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;

        unordered_map<long long, long long> mapRes;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> mapRec; 

            if (nums[i] == k) {
                cnt++;
            }
            mapRec[nums[i]] = 1;

            for (auto& [val, fr] : mapRes) {
                long long updateTemp = val & nums[i];
                if (updateTemp == k) {
                    cnt += fr;
                }
                mapRec[updateTemp] += fr;
            }

            mapRes = mapRec;
        }

        return cnt;
    }
};
