#include <vector>
#include <unordered_map>

#define ll long long

class Solution {
public:
    long long countSubarrays(std::vector<int>& nums, int k) {
        ll ans = 0;
        std::unordered_map<ll, ll> mp;
        int n = nums.size();

        for (int i = 0; i < n; i++) {
            std::unordered_map<ll, ll> curr; // Clear curr for each iteration

            if (nums[i] == k) {
                ans++;
            }
            curr[nums[i]] = 1;

            for (auto& [value, count] : mp) {
                ll newAndResult = value & nums[i];
                if (newAndResult == k) {
                    ans += count;
                }
                curr[newAndResult] += count;
            }
            mp = curr;
        }
        return ans;
    }
};
