class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
      int n = nums.size();
        long long int ans = 0;
        map<long long int , long long int > prev;
        for (int i = 0; i < n; ++i) 
        {
            map<long long int , long long int > cur; 

            if (nums[i] == k) 
                ans++;

            cur[nums[i]] = 1;
            for (auto x: prev) {
                long long int res = x.first & nums[i];
                if (res == k) 
                    ans += x.second;

                cur[res] += x.second;
            }

            prev = cur;
        }
        return ans;









    }
};
