class Solution {
public:
     map<long long, long long> mp;
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            map<long long, long long> cr; 
            if (nums[i] == k) {
                ans++;
            }
            cr[nums[i]] = 1;
            for (auto x: mp) {
                 long long val=x.first;
                 long long freq=x.second;
                long long y = (val & nums[i]);
                if (y == k) {
                    ans += freq;
                }
                cr[y] += freq;
            }

           mp.clear();
            for(auto x:cr){
                mp[x.first]=x.second;
            }
        }
        return ans;
    }
};
