class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        // vector<vector<int>> f(n, vector<int> (32, 0));
        // for(int i=0; i<n; i++){
        //     for(int j=0; j<32; j++){
        //         if(a[i] & (1<<j)) f[i][j] = 1;
        //         if(i) f[i][j] = f[i-1][j];
        //     }
        // }
        // long long cnt = 0;
        map<int, int> st;
        map<int, int> newst;
        long long cnt = 0LL;
        for(int i=n-1; i>=0; i--){
            if(nums[i] == k){
                cnt++;
            }
            for(auto v: st){
                if((v.first&nums[i] )== k) {
                    cnt+=v.second;
                }
            }
            newst.clear();
            newst[nums[i]]++;
            for(auto v: st){
                newst[nums[i]&v.first]+=v.second;
            }
            st.clear();
            for(auto v: newst){
                st[v.first] = v.second;
            }
        }
        return cnt;
    }
};
