class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> dp;
        for (auto i: nums) {
            if (i == k)
                ans++;
            unordered_map<long long, long long> curr; 
            curr[i] = 1;
            for (auto m : dp) {
                int an = m.first & i;
                int f = m.second;
                if (an == k) {
                    ans +=  f;
                }
                curr[an] += f;
            }

            dp = curr;
        }
        return ans;
    }
};
