class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
         int n = a.size();
        long long cnt = 0;
        unordered_map<long long, long long> mp;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr;

            if (a[i] == k) {
                ++cnt;
            }
            curr[a[i]] = 1;

            for (auto& [val, freq] : mp) {
                long long newAndResult = val & a[i];
                if (newAndResult == k) {
                    cnt += freq;
                }
                curr[newAndResult] += freq;
            }

            mp = curr;
        }

        return cnt;
    }
};
