class Solution {
    public long countSubarrays(int[] nums, int k) {
        long ans=0;
        Map<Long,Long> map = new HashMap<>();
        for(int i=0;i<nums.length;++i){
            if( nums[i]==k) ans++;
            Map<Long,Long> currMap = new HashMap<>();
            currMap.put((long)nums[i], 1L);
            for(Map.Entry<Long, Long> entry : map.entrySet()){
                long a = entry.getKey(),b= entry.getValue();
                long and = a & nums[i];
                if(and ==k) ans += b;
                currMap.put(and, currMap.getOrDefault(and, 0L) + b);
            }
            map = currMap;
        }
        return ans;
    }
}
