class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;
        unordered_map<long long,int>prev;
        for(int i=0;i<n;i++)
        {
            unordered_map<long long,int>curr;
            if(nums[i]==k) ans++;
            curr[nums[i]]=1;
            for(auto it:prev)
            {
                long long res=it.first &nums[i];
                if(res==k) ans+=it.second;
                curr[res]+=it.second;
            }
            prev=curr;
        }
        return ans;
    }
};
