class Solution {
public:
    long long countSubarrays(vector<int>& nums, int targetValue) {
        int n = nums.size();
        long long subarrayCount = 0;
        unordered_map<long long, long long> prevAndResults;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currAndResults;
            if (nums[i] == targetValue) {
                ++subarrayCount;
            }
            currAndResults[nums[i]] = 1;
            for (auto& [andResult, freq] : prevAndResults) {
                long long newAndResult = andResult & nums[i];
                if (newAndResult == targetValue) {
                    subarrayCount += freq;
                }
                currAndResults[newAndResult] += freq;
            }

            prevAndResults = currAndResults;
        }
        return subarrayCount;
    }
};
