class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long total=0;
        for(int i=0; i<10; i++){
            rand();
        }

        unordered_map<long long ,long long>left;
        for(int j=0; j<nums.size(); j++){
            unordered_map<long long,long long>initial;
            if(nums[j]==k){
                total++;
            }

            initial[nums[j]]=1;
            for(int i=0; i<10; i++){
                rand();
            }
            for(auto&[ab,freq]:left){
                long long res=ab & nums[j];
                if(res==k){
                    total+=freq;
                }
                initial[res]+=freq;
            }
            left=initial;
        }
        return total;
    }
};
