class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;
        unordered_map<long long, long long> pre_resulting;
        for (int index = 0; index < n; ++index) {
            unordered_map<long long, long long> ori_resulting; 
            if (nums[index] == k) {
                ++cnt;
            }
            ori_resulting[nums[index]] = 1;
            for (auto& [val, frequent] : pre_resulting) {
                long long count = val & nums[index];
                if (count == k) {
                    cnt += frequent;
                }
                ori_resulting[count] += frequent;
            }

            pre_resulting = ori_resulting;
        }
        return cnt;
    }
};
