class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        res = 0
        f = defaultdict(int)
        for i, num in enumerate(nums):
            df = defaultdict(int)
            res += int(num == k)
            df[num] = 1
            for val in f.keys():
                and_val = val & num
                if and_val == k:
                    res += f[val]
                df[and_val] += f[val]
            f = df

        return res
