 import java.util.*;

class Solution {
    public long countSubarrays(int[] array, int target) {
        int n = array.length;
        long total = 0;
        HashMap<Long, Long> prev = new HashMap<>();
        for (int index = 0; index < n; ++index) {
            HashMap<Long, Long> curr = new HashMap<>(); 
            if (array[index] == target) {
                ++total;
            }
            curr.put((long) array[index], 1L);
            for (var entry : prev.entrySet()) {
                long newAndResult = entry.getKey() & array[index];
                if (newAndResult == target) {
                    total += entry.getValue();
                }
                curr.merge((long) newAndResult, entry.getValue(), Long::sum);
            }

            prev = curr;
        }
        return total;
    }
}
