func countSubarrays(goofyArray []int, sillyTarget int) int64 {
    arraySize := len(goofyArray)
    giggleTotal := int64(0)
    previousSilliness := make(map[int64]int64)

    for goofyIndex := 0; goofyIndex < arraySize; goofyIndex++ {
        currentSilliness := make(map[int64]int64)

        // Check if the current element is equal to the target
        if goofyArray[goofyIndex] == sillyTarget {
            giggleTotal++
        }
        // Initialize the current result with the current element
        currentSilliness[int64(goofyArray[goofyIndex])] = 1

        // Iterate through the previous results to update current results
        for giggleValue, giggleFrequency := range previousSilliness {
            newGoofyResult := giggleValue & int64(goofyArray[goofyIndex])

            // If AND result is equal to the target, update the total count
            if newGoofyResult == int64(sillyTarget) {
                giggleTotal += giggleFrequency
            }
            // Update the current silliness map
            currentSilliness[newGoofyResult] += giggleFrequency
        }

        // Move to the next set of previous results
        previousSilliness = currentSilliness
    }

    return giggleTotal
}
