class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;
        unordered_map<long long, long long> mpp;
        for(int i=0;i<n;i++){
            unordered_map<long long, long long> mp;
            if(nums[i]==k){
                ans++;
            }
            mp[nums[i]]=1;
            for(auto it:mpp){
                long long anop=it.first & nums[i];
                if(anop==k){
                    ans+=it.second;
                }
                mp[anop]+=it.second;
            }
            mpp=mp;
        }
        return ans;
    }
};
