class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();

        long long ans=0;
        unordered_map<long long, long long>m;

        for (int i=0;i<n;i++) {

            unordered_map<long long, long long>p; 

            if (nums[i] ==k) {
                ans++;
            }

            p[nums[i]] = 1;

            for (auto j:m){
                int v=j.first;
                int f=j.second;
                long long t =v&nums[i];
                if(t==k){
                    ans+=f;
                }
                p[t]+=f;
            } 

            m=p;
        }
        return ans;
    }
};
