class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>>ne(n+1,vector<int>(30,n));
        for(int j=0;j<30;j++){
            for(int i=n-1;i>=0;i--){
                int x=nums[i];
                ne[i][j]=ne[i+1][j];
                if(!((x>>j)&1)) ne[i][j]=i;
            }
        }
        long long ans=0;
        for(int i=0;i<n;i++){
            int mi=n;
            int mx=i;
            for(int j=0;j<30;j++){
                if((k>>j)&1){
                    mi=min(mi,ne[i][j]);
                } else {
                    mx=max(mx,ne[i][j]);
                }
            }
            if(mi>mx){
                ans+=(mi-mx);
            }
        }
        return ans;
    }
};
