#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int n=arr.size();
        ll ans=0;
        unordered_map<ll,ll> pr;
        unordered_set<int> st;
        unordered_map<ll,ll> cr;
        bool res=false;
        for(ll i=0;i<n;i++){
            cr[arr[i]]=1;
            ans+=arr[i]==k?1:0;
            for(auto it:pr){
                ll v=it.first,f=it.second;
                if(v&arr[i]!=k) st.insert(v&&arr[i]);
                if((ll)(v&arr[i])==k) {
                    ans+=f;
                    res=true;
                }
                cr[(ll)(v&arr[i])]=cr[(ll)(v&arr[i])]+f;
            }
            pr=cr;
            cr.clear();
        }
        return ans;
    }
};
