#define lli long long int
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        int n= nums.size();
        lli res= 0;
        map<lli, lli> m;
        for(int i=0; i<nums.size(); i++){

            map<lli, lli> mp;
            if(nums[i] == k) res++;
            mp[nums[i]]= 1;
            for(auto it= m.begin(); it!= m.end(); it++){
                lli key= it->first & nums[i];
                if(key == k) res+= it->second;
                mp[key]+= it->second;
            }

            m= mp;
        }

        return res;

    }
};
