class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0
        prev_results = {}

        for num in nums:
            curr_results = {}

            if num == k:
                count += 1
            curr_results[num] = curr_results.get(num, 0) + 1

            for val, freq in prev_results.items():
                new_and_result = val & num
                if new_and_result == k:
                    count += freq
                curr_results[new_and_result] = curr_results.get(new_and_result, 0) + freq

            prev_results = curr_results

        return count
