function countSubarrays(array, target) {
    let size = array.length;
    let total = 0;
    let previousResults = new Map();
    for (let index = 0; index < size; ++index) {
        let currentResults = new Map();
        if (array[index] === target) {
            ++total;
        }
        currentResults.set(array[index], 1);
        for (let[value,frequency] of previousResults) {
            let newAndResult = value & array[index];
            if (newAndResult === target) {
                total += frequency;
            }
            currentResults.set(newAndResult, (currentResults.get(newAndResult) || 0) + frequency);
        }

        previousResults = currentResults;
    }
    return total;
}
