class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        int n = nums.size();
        long long Count = 0;
        unordered_map<long long, long long> prev;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr;
            if (nums[i] == target) {
                Count++;
            }
            curr[nums[i]] = 1;

            for (auto& [prevresult, frequency] : prev) {
                long long currresult = prevresult & nums[i];
                if (currresult == target) {
                    Count += frequency;
                }
                curr[currresult] += frequency;
            }

            prev = curr;
        }
        return Count;
    }
};
