class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        unordered_map<int,int>mpre;
        for(int i=0;i<nums.size();i++){
        unordered_map<int,int>mcur;
            if(nums[i]==k){
                ans++;
            }
            mcur[nums[i]]=1;
            for(auto it:mpre){
               long long value=it.first;
                long long fr=it.second;
                long long resu=value&nums[i];
                if(resu==k)ans+=fr;
                mcur[resu]+=fr;
            }
            mpre=mcur;
        }
        return ans;
    }
};
