class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        long long cnt = 0;
        unordered_map<long long, long long> previous_vector;
        int m = nums.size();

        for (int i = 0; i < m; ++i) {

            unordered_map<long long, long long> current_vector; 

            if (nums[i] == k) {
                cnt++;
            }
            current_vector[nums[i]] = 1;

            for (auto [val, freq] : previous_vector) {

                long long result = val & nums[i];

                if (k==result) {
                    cnt = cnt+freq;
                }
                current_vector[result] = current_vector[result]+ freq;
            }

            previous_vector =current_vector;
        }

        long long ans=cnt;
        return ans;

    }
};
