class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long int ans = 0;
        int i=0;
        unordered_map<long long int,long long int> mp1;
        while(i<n){
        unordered_map<long long int,long long int> mp2; 
            if(k==nums[i]) ans++;
            mp2[nums[i]]++;
            for(auto& [a,b] : mp1){
                long long int curr = nums[i] & a;
                if(k==curr) ans = ans + b;    

            mp2[curr] = mp2[curr]+b;
            }
            mp1=mp2;
            i++;
        }
        return ans;
    }
};
