class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
    long long n= nums.size(),ans=0;
    unordered_map<long long,long long>mpp;
    for (int i=0;i<n;i++) {
        unordered_map<long long, long long>mp;
        if (nums[i]==k)ans++;
           mp[nums[i]]=1;
        for (auto it:mpp){
            long long x=it.first&nums[i];
            mp[x]+=it.second;
             if(x==k) ans+=it.second;
        }
        mpp=mp;
    }
    return ans;
    }
};
