class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        long long point=0;
        int n=nums.size();
        unordered_map<long long,long long>array_prev;
        for(int i=0;i<n;++i)
        {
           unordered_map<long long,long long>array_curr; 
           if(nums[i]==target){
           ++point;
           }

        array_curr[nums[i]]=1;
        for(auto &[value,frequency]:array_prev)
        {
            long long result= value & nums[i];
            if(result==target)
            {
                point+=frequency;
            }
            array_curr[result]+=frequency;
        }
        array_prev=array_curr;
    }
    return point;
    }

};
