class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        set<long long> ms;
        map<long long, long long> mp;
        int n = nums.size();
        long long ans = 0;

        for(int i = n-1; i>=0; i--){
            if(ms.size() == 0){
                ms.insert(nums[i]);
                mp[nums[i]]++;
                if(nums[i] == k) ans++;
            }else{

                set<long long> news;
                news.insert(nums[i]);
                if(nums[i] == k) ans++;
                map<long long, long long> newmp;
                for(auto c : ms){
                    if((c&nums[i]) == k){
                        ans += (mp[c]);
                    }
                    news.insert(c&nums[i]);
                    newmp[(c&nums[i])]+=(mp[c]);
                }
                mp[nums[i]]++;

                ms.clear();
                for(auto c : news){
                    ms.insert(c);
                }
                // cout<<mp[1]<<endl;
                mp.clear();
                for(auto c : newmp){
                    mp[c.first]+=c.second;
                }
                mp[nums[i]]++;
            }
            // cout<<ans<<" "<<mp[1]<<endl;
        }

        return ans;

    }
};
