class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long res = 0;
        Map<Long,Long> hm = new HashMap<>();
        for(int i = 0;i < n;++i){
            Map<Long,Long> curr = new HashMap<>();
            if(nums[i] == k){
                ++res;
            }
            curr.put((long)nums[i],1L);
            for(Map.Entry<Long,Long> e : hm.entrySet()){
                long val = e.getKey();
                long freq = e.getValue();
                long ref = val & nums[i];
                if(ref == k){
                    res += freq;
                }
                curr.put(ref,curr.getOrDefault(ref,0L) + freq);
            }
            hm = curr;
        }
        return res;
    }
}
