class Solution {
    public long countSubarrays(int[] array, int target){
        int size=array.length;
        long total=0;
        Map<Long,Long> p=new HashMap<>();
        for (int i=0;i<size;i++){
            Map<Long,Long> hm=new HashMap<>();
            if(array[i]==target) total++;
            hm.put((long)array[i],1L);
            for(Map.Entry<Long,Long> entry:p.entrySet()){
                long v=entry.getKey();
                long f=entry.getValue();
                long newAndResult=v & array[i];
                if(newAndResult==target) total+=f;
                hm.put(newAndResult,hm.getOrDefault(newAndResult,0L)+f);
            }
            p=hm;
        }
        return total;
    }
}
