#include <vector>
#include <map>
using namespace std;

class Helper {
public:
    long long help(vector<int>& nums, int k, int n, long long res, map<long long, long long>& prevres) {
        for (int i = 0; i < n; ++i) {
            map<long long, long long> currres; 

            if (nums[i] == k) {
                res++;
            }
            currres[nums[i]] = 1;

            for (auto& it : prevres) {
                long long val = it.first;
                long long freq = it.second;
                long long newres = val & nums[i];
                if (newres == k) {
                    res += freq;
                }
                currres[newres] += freq;
            }
            prevres = currres;
        }
        return res;
    }
};

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        map<long long, long long> prevres;
        Helper helper;
        long long res = helper.help(nums, k, n, count, prevres);
        return res;
    }
};
