class Solution
 {
public:
    long long countSubarrays(vector<int>& nums, int k)
 {
        int n = nums.size();
        long long countttt = 0;
        //kkkdsubvdubdbjxcbjbc
        unordered_map<long long, long long> earlier;
//kkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkkk
        for (int i = 0; i < n; ++i)
 {
            unordered_map<long long, long long> nnoww; 
//ooooooooooooooooooooonnnnnnnnnnnnnnnnnnnnnggggggggggggggggg
            if (nums[i] == k)
 {
                ++countttt;
            }
            nnoww[nums[i]] = 1;
//ttttttttttttttttttttttbbbbbbbbbbbbbbbbbbbwwwwwwwwwwwwww
            for (auto& [val, freqencyyy] : earlier)
 {
                long long aannddrreess = val & nums[i];
                if (aannddrreess == k)
 {
                    countttt += freqencyyy;
                }
                nnoww[aannddrreess] += freqencyyy;
            }

            earlier = nnoww;
        }
   //88888888888888855555555555555555555553333333333333333333     
        return countttt;
 }



};
