class Solution {

public:
    #define ll long long
    long long countSubarrays(vector<int>& nums, int k) {
        ll n = nums.size();
        ll count = 0;
        map<ll, ll> mp1;

        for (int i=0;i<n;i++) {
            map<ll, ll> mp2;

            if (nums[i] == k) {
                count++;
            }
            mp2[nums[i]] = 1;

            for (auto& it : mp1) {
                int chk=it.first & nums[i];
                if (chk == k) {
                    count += it.second;
                }
                mp2[chk] += it.second;
            }
            mp1 = mp2;
        }

        return count;

    }
};
