class Solution {
public:
    #define ll long long
    long long countSubarrays(vector<int>& nums, int k) {
      int n =nums.size();
        unordered_map<ll,ll> mp;
        ll ans=0;
        for(int i=0;i<n;i++){
            if(nums[i]==k) ans++;
             unordered_map<ll,ll> mapcurr;
            mapcurr[nums[i]]=1;
            for(auto it: mp){
                ll val=it.first & nums[i];
                if(val==k) ans+= it.second;
                mapcurr[val]+= it.second;
            }
            mp=mapcurr;
        }
        return ans;

    }
};
