class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long total = 0;
        unordered_map<long long, long long> prev;
        for (int i = 0; i < n; i++) {
            unordered_map<long long, long long> curr; 
            if (nums[i] == k) {
                total++;
            }
            curr[nums[i]] = 1;
            for (auto& [value, freq] : prev) {
                long long newRes = value & nums[i];
                if (newRes == k) {
                    total += freq;
                }
                curr[newRes] += freq;
            }

            prev = curr;
        }
        return total;
    }
};
